public class Question7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 15;
		
		if (number % 2 == 0)
			System.out.println(number + " is even");
		else if (number % 5 == 0)
			System.out.println(number + " is a multiple of 5");
	}
}